        name: "testdb",
    }],
    environment: "PRESTABLE",
    hosts: [{
        subnetId: fooVpcSubnet.id,
        zoneId: "ru-central1-a",
    }],
    labels: {
        test_key: "test_value",
    },
    maintenanceWindow: {
        type: "ANYTIME",
    },
    networkId: fooVpcNetwork.id,
    resources: {
        diskSize: 16,
        diskTypeId: "network-hdd",
        resourcePresetId: "b1.nano",
    },
    users: [{
        name: "john",
        password: "password",
        permissions: [{
            databaseName: "testdb",
        }],
    }],
});
