    environment: "PRESTABLE",
    hosts: [
        {
            shardName: "first",
            subnetId: fooVpcSubnet.id,
            zone: "ru-central1-a",
        },
        {
            shardName: "second",
            subnetId: bar.id,
            zone: "ru-central1-b",
        },
        {
            shardName: "third",
            subnetId: baz.id,
            zone: "ru-central1-c",
        },
    ],
    networkId: fooVpcNetwork.id,
    resources: {
        diskSize: 16,
        resourcePresetId: "hm1.nano",
    },
